                </a>
            </div>
            <div class="balance">
                <span v-if="!isEmpty(currentBalance)">
                    <span class="balance__title">
                        {{ $t("send.balance") }}
                    </span>
                    <span class="balance__amount">
                        {{ currentBalance | formatAmount }}
                    </span>
                </span>
            </div>
            <el-dropdown
                class="dropdown"
                trigger="click"
                @command="getStarted">
                <div class="dropdown__btn">
                    <div class="avatar">
                        <img
                            alt="account"
                            :src="walletIcon">
                    </div>
                    <span>
                        {{ userName }}
                    </span>
                    <i class="el-icon-arrow-down"></i>
                </div>
                <el-dropdown-menu
                    slot="dropdown"
                    class="dropdown__menu">
                    <el-dropdown-item
                        class="dropdown__menu__item"
                        command="account">
                        <img
                            :alt="userName"
                            :src="walletIcon">
                        {{ $t('home.account') }}
                    </el-dropdown-item>
                    <el-dropdown-item
                        class="dropdown__menu__item"
                        command="staking">
                        {{ $t('home.staking') }}
                    </el-dropdown-item>
                    <el-dropdown-item
                        class="dropdown__menu__item"
                        command="gov">
                        {{ $t('gov.title') }}
                    </el-dropdown-item>
                    <el-dropdown-item
                        class="dropdown__menu__item"
                        command="assets">
                        {{ $t('assets.title') }}
                    </el-dropdown-item>
                    <el-dropdown-item
                        class="dropdown__menu__item"
                        command="issues">
                        {{ $t('issues.title') }}
                    </el-dropdown-item>
                    <el-divider class="dropdown__menu__divider"/>
                    <el-dropdown-item
                        class="dropdown__menu__item"
                        command="passport">
                        <img
                            alt="icon3"
                            :src="icon3"/>
                        {{ $t('main.manage') }}
                    </el-dropdown-item>
                    <el-dropdown-item
                        class="dropdown__menu__item"
                        command="logout">
                        {{ $t('main.logout') }}
                    </el-dropdown-item>
                </el-dropdown-menu>
            </el-dropdown>
        </div>
    </div>
</template>

<script>
    import { mapState } from 'vuex';
    import { get, isEmpty } from 'lodash';

    import walletIcon from '../../assets/icon-wallet.svg';
    import { DEFAULT_DENOM, explorerDomain } from '../../constants';
    import icon3 from '../../assets/icon-setting.svg';

    export default {
        name: 'Header',
        props: {
            netName: String,
        },
        data() {
            return {
                icon3,
                bgColor: '',
                walletIcon,
                domain: explorerDomain,
            };
        },
        computed: {
            ...mapState('account', ['userName', 'balance']),
            ...mapState('basic', ['info']),
            currentBalance() {
                return this.balance.filter(i => i.denom === DEFAULT_DENOM);
            },
        },
        mounted() {
            window.onscroll = this.handleScroll;
            this.$store.dispatch('account/fetchBalance');
        },
        methods: {
            get,
            isEmpty,
            changeLang() {
                this.$i18n.locale = 'en';
                localStorage.setItem('lang', 'en');
            },
            getStarted(page) {
                const uris = {
                    account: '/main?tab=assets',
                    staking: '/staking?tab=delegations',
                    gov: '/gov?tab=my',
                    assets: '/assets',
                    issues: '/issues',
                    passport: '/passport',
                };
                const agree = localStorage.getItem('wallet_agree');
                if (!agree) {
                    this.$router.push('/agree');
                    return;
                }

                if (this.userName) {
                    this.$router.push(uris[page]);
                    return;
                }

                this.$router.push('/passport');
            },
            handleScroll() {
                if (window.scrollY > 20) {
                    this.bgColor = 'primary';
                } else {
                    this.bgColor = '';
                }
            },
        },
    };
</script>

<style lang="scss" scoped>
    .el-divider {
        margin: 0;
    }

    .dropdown {
        &__menu {
            &__item {
                display: flex;
