        &.primary {
            background: $color-primary;
            box-shadow: $shadow;
        }
    }

    .header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        max-width: $max-width;
        margin: 0 auto;
        height: 60px;

        @include responsive($md) {
            padding: 0 12px;
        }

        .logo {
            flex: 1;
            display: inline-block;
            line-height: 1;

            img {
                height: 52px;

                @include responsive($xs) {
                    display: none;
                }
            }

            &__small {
                display: none;

                @include responsive($xs) {
                    display: block;
                }
            }
        }

        .balance {
            padding: 0 10px 0 0;

            &__title {
                @include responsive($xs) {
                    display: none;
                }
            }
        }

        .network {
            font-size: 16px;

            span {
                color: white;
                cursor: pointer;
            }
        }

        .dropdown {
            margin-left: $padding-basic;
            cursor: pointer;
            font-size: 16px;
            color: rgba(255, 255, 255, 0.8);

            &__btn {
                display: flex;
                align-items: center;

                i {
                    margin-left: 4px;
                    margin-top: 2px;
                }
            }

            .avatar {
                height: 28px;
                width: 28px;
                background: rgba(255, 255, 255, 0.2);
                overflow: hidden;
                border-radius: 14px;
                padding: 3px;
                margin-right: 6px;

                img {
                    height: 18px;
                    margin: 2px;
                }
            }

            &:hover {
                color: white;

                .avatar {
                    background: rgba(255, 255, 255, 0.3);
                }
            }
        }
    }

    @include responsive($sm) {
        .header {
            padding: 16px;

            .dropdown {
                margin-left: 12px;
            }
        }
    }

    .el-icon-arrow-down {
        color: $color-active;
    }
</style>
